mapper.select:
SELECT sum(count) count_point FROM ( SELECT count(1) count FROM tbl_powerstation t WHERE 1=1 and poSt_Status in (10, 15)
and exists (select 1 from tbl_user_business_view b where b.user_id=t.poSt_createUserId and exists (select 1 from tbl_user_business_view b1 where b.busi_company_id = b1.busi_company_id and b1.user_id = #{userId}))
and t.poSt_createUserId=#{userId}
and t.poSt_OwnProvinceCode = #{province}
UNION ALL SELECT count(1) count FROM tbl_electricpile p WHERE p.elPi_Binding = 0 AND elpi_state IN (10, 15)
and exists (select 1 from tbl_user_business_view b where b.user_id=p.elPi_UserId and exists (select 1 from tbl_user_business_view b1 where b.busi_company_id = b1.busi_company_id and b1.user_id = #{userId}))
and p.elPi_UserId=#{userId}
and elPi_OwnProvinceCode = #{province}
) a

SELECT * FROM (SELECT count(1) countDc FROM tbl_electricpile t WHERE t.elPi_ChargingMode = '5' and t.delete_flag = '0' AND t.elpi_state IN (10, 15)
and exists (select 1 from tbl_user_business_view b where b.user_id=elPi_UserId and exists (select 1 from tbl_user_business_view b1 where b.busi_company_id = b1.busi_company_id and b1.user_id = #{userId}))
and elPi_UserId=#{userId}
and t.elPi_OwnProvinceCode = #{province}
) a, (SELECT count(1) countAc FROM tbl_electricpile t WHERE t.elPi_ChargingMode = '14' and t.delete_flag = '0' AND t.elpi_state IN (10, 15)
and exists (select 1 from tbl_user_business_view b where b.user_id=elPi_UserId and exists (select 1 from tbl_user_business_view b1 where b.busi_company_id = b1.busi_company_id and b1.user_id = #{userId}))
and elPi_UserId=#{userId}
and t.elPi_OwnProvinceCode = #{province}
) b

SELECT count(1) all_charge_count, sum(a.chOr_QuantityElectricity) allCharge_c,
FORMAT ( sum( ( UNIX_TIMESTAMP(end_charge_time) - UNIX_TIMESTAMP(begin_charge_time) ) / 3600 ), 2 ) allCharge_t FROM tbl_chargingorder a, tbl_electricpile b WHERE a.chOr_PileNumber = b.elPi_ElectricPileCode AND a.chOr_ChargingStatus >= 2 and a.chOr_ChargingStatus!=4 AND (UNIX_TIMESTAMP(end_charge_time) - UNIX_TIMESTAMP(begin_charge_time))>0
and exists (select 1 from tbl_user_business_view b where b.user_id=elPi_UserId and exists (select 1 from tbl_user_business_view b1 where b.busi_company_id = b1.busi_company_id and b1.user_id = #{userId}))
and elPi_UserId=#{userId}
and b.elPi_OwnProvinceCode = #{province}

select * from (select count(1) free_count from tbl_electricpilehead t where t.delete_flag = '0' and t.ePHe_ElectricpileHeadState='0' and exists (select 1 from tbl_electricpile e where e.pk_ElectricPile = t.pk_ElectricPile AND e.comm_status ='1' AND e.elpi_state IN (10, 15)
and exists (select 1 from tbl_user_business_view b where b.user_id=e.elPi_UserId and exists (select 1 from tbl_user_business_view b1 where b.busi_company_id = b1.busi_company_id and b1.user_id = #{userId}))
and e.elPi_UserId=#{userId}
and e.elPi_OwnProvinceCode = #{province}
)) a, (select count(1) bespoke_count from tbl_electricpilehead t where t.delete_flag = '0' and t.ePHe_ElectricpileHeadState='3' and exists (select 1 from tbl_electricpile e where e.pk_ElectricPile = t.pk_ElectricPile AND e.comm_status ='1' AND e.elpi_state IN (10, 15)
and exists (select 1 from tbl_user_business_view b where b.user_id=e.elPi_UserId and exists (select 1 from tbl_user_business_view b1 where b.busi_company_id = b1.busi_company_id and b1.user_id = #{userId}))
and e.elPi_UserId=#{userId}
and e.elPi_OwnProvinceCode = #{province}
)) b, (select count(1) charging_count from tbl_electricpilehead t where t.delete_flag = '0' and t.ePHe_ElectricpileHeadState='6' and exists (select 1 from tbl_electricpile e where e.pk_ElectricPile = t.pk_ElectricPile AND e.comm_status ='1' AND e.elpi_state IN (10, 15)
and exists (select 1 from tbl_user_business_view b where b.user_id=e.elPi_UserId and exists (select 1 from tbl_user_business_view b1 where b.busi_company_id = b1.busi_company_id and b1.user_id = #{userId}))
and e.elPi_UserId=#{userId}
and e.elPi_OwnProvinceCode = #{province}
)) c, (select count(1) error_count from tbl_electricpilehead t where t.delete_flag = '0' and t.ePHe_ElectricpileHeadState='9' and exists (select 1 from tbl_electricpile e where e.pk_ElectricPile = t.pk_ElectricPile AND e.comm_status ='1' AND e.elpi_state IN (10, 15)
and exists (select 1 from tbl_user_business_view b where b.user_id=e.elPi_UserId and exists (select 1 from tbl_user_business_view b1 where b.busi_company_id = b1.busi_company_id and b1.user_id = #{userId}))
and e.elPi_UserId=#{userId}
and e.elPi_OwnProvinceCode = #{province}
)) d, (SELECT count(1) countOff FROM tbl_electricpile t WHERE t.comm_status='0' and t.delete_flag = '0' AND t.elpi_state IN (10, 15)
and exists (select 1 from tbl_user_business_view b where b.user_id=elPi_UserId and exists (select 1 from tbl_user_business_view b1 where b.busi_company_id = b1.busi_company_id and b1.user_id = #{userId}))
and elPi_UserId=#{userId}
and t.elPi_OwnProvinceCode = #{province}
) e

select a.province_id,a.PROVINCE_NAME province_name,b.pile_count pile_count from tb_m_province a LEFT JOIN (SELECT a.elPi_OwnProvinceCode, sum(pile_count) pile_count FROM ( select t.elPi_OwnProvinceCode,count(1) pile_count from tbl_electricpile t where t.elPi_State in ('10','15') and t.elPi_Binding = 0
and exists (select 1 from tbl_user_business_view b where b.user_id=elPi_UserId and exists (select 1 from tbl_user_business_view b1 where b.busi_company_id = b1.busi_company_id and b1.user_id = #{userId}))
and elPi_UserId=#{userId}
GROUP BY t.elPi_OwnProvinceCode UNION all SELECT a.poSt_OwnProvinceCode elPi_OwnProvinceCode, count(1) pile_count FROM tbl_powerstation a where 1=1 and poSt_Status in (10, 15)
and exists (select 1 from tbl_user_business_view b where b.user_id=a.poSt_createUserId and exists (select 1 from tbl_user_business_view b1 where b.busi_company_id = b1.busi_company_id and b1.user_id = #{userId}))
and a.poSt_createUserId=#{userId}
GROUP BY a.poSt_OwnProvinceCode ) a GROUP BY a.elPi_OwnProvinceCode) b on a.PROVINCE_ID=b.elPi_OwnProvinceCode;

