SELECT count(1) all_charge_count, sum(a.chOr_QuantityElectricity) allCharge_c, FORMAT (sum(( UNIX_TIMESTAMP(end_charge_time) - UNIX_TIMESTAMP(begin_charge_time) ) / 3600),2) allCharge_t
FROM tbl_chargingorder a, tbl_electricpile b WHERE a.chOr_PileNumber = b.elPi_ElectricPileCode AND a.chOr_ChargingStatus >= 2 and a.chOr_ChargingStatus !=4 and UNIX_TIMESTAMP(a.end_charge_time) - UNIX_TIMESTAMP(a.begin_charge_time)>0
and b.pk_ElectricPile = #{eId}
and b.elPi_RelevancePowerStation = #{pId}
and exists(select 1 from tbl_electricpilehead c where c.pk_ElectricPile = b.pk_ElectricPile and c.ePHe_ElectricpileHeadId=a.chOr_Muzzle and c.pk_ElectricpileHead = #{headId})

select count(1) all_head_count from tbl_electricpilehead t where t.delete_flag = '0'
and t.pk_ElectricPile = #{eId}
and exists(select 1 from tbl_electricpile e where e.pk_ElectricPile=t.pk_ElectricPile and e.elPi_RelevancePowerStation = #{pId})

select * from (select count(1) free_count from tbl_electricpilehead t where t.delete_flag = '0' and t.ePHe_ElectricpileHeadState='0' and exists (select 1 from tbl_electricpile e where e.pk_ElectricPile=t.pk_ElectricPile and e.comm_status = '1')
and t.pk_ElectricPile = #{eId}
and exists(select 1 from tbl_electricpile e where e.pk_ElectricPile=t.pk_ElectricPile and e.elPi_RelevancePowerStation = #{pId})
) a, (select count(1) bespoke_count from tbl_electricpilehead t where t.delete_flag = '0' and t.ePHe_ElectricpileHeadState='3'
and t.pk_ElectricPile = #{eId}
and exists(select 1 from tbl_electricpile e where e.pk_ElectricPile=t.pk_ElectricPile AND e.comm_status = '1' and e.elPi_RelevancePowerStation = #{pId})
) b, (select count(1) charging_count from tbl_electricpilehead t where t.delete_flag = '0' and t.ePHe_ElectricpileHeadState='6'
and t.pk_ElectricPile = #{eId}
and exists(select 1 from tbl_electricpile e where e.pk_ElectricPile=t.pk_ElectricPile AND e.comm_status = '1' and e.elPi_RelevancePowerStation = #{pId})
) c, (select count(1) error_count from tbl_electricpilehead t where t.delete_flag = '0' and t.ePHe_ElectricpileHeadState='9'
and t.pk_ElectricPile = #{eId}
and exists(select 1 from tbl_electricpile e where e.pk_ElectricPile=t.pk_ElectricPile AND e.comm_status = '1' and e.elPi_RelevancePowerStation = #{pId})
) d, ( SELECT count(1) countOff FROM tbl_electricpile t, tbl_electricpilehead t2 WHERE t.pk_ElectricPile = t2.pk_ElectricPile and t.comm_status='0' and t2.delete_flag = '0'
and t.pk_ElectricPile = #{eId}
and t.elPi_RelevancePowerStation = #{pId}
) e
