),0) as aliPayUserPu_Monetary,
ifnull((select sum(puHi_Monetary) from
tbl_purchasehistory where puHi_UserId =
#{puHi_UserId} and
puHi_ChargeType = 2

		and puHi_PurchaseHistoryTime>=#{startTime}


		and puHi_PurchaseHistoryTime<=#{endTime}

),0) as weChatUserPu_Monetary,
ifnull((select sum(puHi_Monetary) from
tbl_purchasehistory where puHi_UserId =
#{puHi_UserId} and
puHi_ChargeType = 3

		and puHi_PurchaseHistoryTime>=#{startTime}


		and puHi_PurchaseHistoryTime<=#{endTime}

),0) as unionpayUserPu_Monetary,
ifnull((select sum(puHi_Monetary) from
tbl_purchasehistory where puHi_UserId =
#{puHi_UserId} and
puHi_ChargeType = 4

		and puHi_PurchaseHistoryTime>=#{startTime}


		and puHi_PurchaseHistoryTime<=#{endTime}

),0) as chargeCardUserPu_Monetary,
ifnull((select sum(puHi_Monetary)
from
tbl_purchasehistory where puHi_UserId =
#{puHi_UserId} and
puHi_ChargeType = 5

		and puHi_PurchaseHistoryTime>=#{startTime}


		and puHi_PurchaseHistoryTime<=#{endTime}

),0) as TransferAccount_Monetary,
ifnull((select sum(puHi_Monetary)
from
tbl_purchasehistory where puHi_UserId =
#{puHi_UserId} and
puHi_ChargeType = 6

		and puHi_PurchaseHistoryTime>=#{startTime}


		and puHi_PurchaseHistoryTime<=#{endTime}

),0) as activity_Monetary
from
tbl_purchasehistory
