mapper.select:
SELECT ph.pk_PurchaseHistory, ph.puHi_Type, ph.puHi_PurchaseHistoryTime, ph.puHi_Monetary, ph.puHi_ConsumerRemark, ph.puHi_Createdate, ph.puHi_Updatedate, ph.puHi_PurchaseContent, ph.puHi_UserId, a.user_account userPhone, b.norm_real_name userName, puhi_ChargeType FROM tbl_purchasehistory ph
LEFT JOIN tbl_user a on ph.puHi_UserId = a.user_id left JOIN tbl_user_normal b on ph.puHi_UserId = b.user_id
and a.user_leval = 6
and ph.puHi_Type=#{puhiType}
and ph.puHi_UserId=#{puhiUserid}
and (b.norm_real_name like concat('%',#{userName},'%') or a.user_account like concat('%',#{userName},'%') )
and puHi_PurchaseHistoryTime>=#{beginTime}
and puHi_PurchaseHistoryTime<= date_add(#{endTime}, interval 1 day)
order by ph.puHi_Createdate desc limit #{pager.offset},#{pager.numPerPage}

select count(1) from tbl_purchasehistory ph
LEFT JOIN tbl_user a on ph.puHi_UserId = a.user_id left JOIN tbl_user_normal b on ph.puHi_UserId = b.user_id
and a.user_leval = 6
and ph.puHi_Type=#{puhiType}
and ph.puHi_UserId=#{puhiUserid}
and puHi_PurchaseHistoryTime>=#{beginTime}
and puHi_PurchaseHistoryTime<=date_add(#{endTime}, interval 1 day)
and (b.norm_real_name like concat('%',#{userName},'%') or a.user_account like concat('%',#{userName},'%') )
