mapper.select:
SELECT e.pk_ElectricPile, e.elPi_ElectricPileName, e.elPi_ElectricPileCode, p.poSt_Name, (select maker_Name from tbl_pilemaker where e.elPi_Maker = pk_Carmaker) as elPi_Maker_Name, (SELECT arg_value FROM tbl_electricpile_workarg tc WHERE tc.ep_code = e.elPi_ElectricPileCode and arg_id=1) AS ARG_VALUE, (SELECT ISSUED_STATUS FROM tbl_electricpile_workarg tc WHERE tc.ep_code = e.elPi_ElectricPileCode and arg_id=1) AS ISSUED_STATUS FROM tbl_electricpile e LEFT JOIN tb_m_province a on a.PROVINCE_ID=e.elPi_OwnProvinceCode LEFT JOIN tb_m_city c on c.CITY_ID=e.elPi_OwnCityCode LEFT JOIN tb_m_area d on d.area_id=e.elPi_OwnCountyCode LEFT JOIN tbl_powerstation p on p.pk_PowerStation = e.elPi_RelevancePowerStation where 1=1
and e.elPi_ElectricPileCode LIKE CONCAT('%',#{elpiElectricpilecode},'%' )
and p.poSt_Name LIKE CONCAT('%',#{postName},'%' )
and a.PROVINCE_ID=#{elPiOwnProvinceCode}
and c.CITY_ID=#{elPiOwnCityCode}
and d.area_id=#{elPiOwnCountyCode}
and exists (select 1 from tbl_user b where b.user_id = e.elPi_UserId )
and exists (select 1 from tbl_user_business_view b where b.user_id=e.elPi_UserId and exists (select 1 from tbl_user_business_view b1 where b.busi_company_id = b1.busi_company_id and b1.user_id = #{elpiUserid})
)
and e.elPi_UserId=#{elpiUserid}
order by e.elPi_Updatedate limit #{pager.offset},#{pager.numPerPage}

	SELECT count(*) FROM
		tbl_electricpile e
		LEFT JOIN tb_m_province a on   a.PROVINCE_ID=e.elPi_OwnProvinceCode 
		LEFT JOIN tb_m_city c on   c.CITY_ID=e.elPi_OwnCityCode
		LEFT JOIN tb_m_area d on    d.area_id=e.elPi_OwnCountyCode
		LEFT JOIN tbl_powerstation p on   p.pk_PowerStation = e.elPi_RelevancePowerStation
		where 1=1

			and e.elPi_ElectricPileCode LIKE CONCAT('%',#{elpiElectricpilecode},'%' )


			and p.poSt_Name LIKE CONCAT('%',#{postName},'%' )


		 	and	 a.PROVINCE_ID=#{elPiOwnProvinceCode}


	 		and c.CITY_ID=#{elPiOwnCityCode}


	 		and d.area_id=#{elPiOwnCountyCode}




        	 	and exists (select 1 from tbl_user b where b.user_id = e.elPi_UserId )


		        and exists (select 1 from tbl_user_business_view b where b.user_id=e.elPi_UserId and exists (select 1 from tbl_user_business_view b1 where  b.busi_company_id = b1.busi_company_id and b1.user_id = #{elpiUserid})

		        )


		        and e.elPi_UserId=#{elpiUserid}
